mapper.select:
SELECT id, user_id, product_name, price, quantity, status, created_at, updated_at
FROM orders
WHERE id = #{id}

SELECT id, user_id, product_name, price, quantity, status, created_at, updated_at
FROM orders
WHERE user_id = #{userId}

SELECT id, user_id, product_name, price, quantity, status, created_at, updated_at
FROM orders
WHERE status = #{status}

SELECT id, user_id, product_name, price, quantity, status, created_at, updated_at
FROM orders
WHERE user_id = #{userId} AND status = #{status}

mapper.update:
UPDATE orders
SET user_id = #{userId}, product_name = #{productName}, price = #{price},
    quantity = #{quantity}, status = #{status}, updated_at = #{updatedAt}
WHERE id = #{id}
