mapper.select:
SELECT id, name, email, phone, password, email_verified, email_verified_at, created_at, updated_at
FROM users
WHERE id = #{id}

SELECT id, name, email, phone, password, email_verified, email_verified_at, created_at, updated_at
FROM users
WHERE email = #{email}

SELECT id, name, email, phone, password, email_verified, email_verified_at, created_at, updated_at
FROM users
WHERE phone = #{phone}

mapper.update:
UPDATE users
SET name = #{name}, email = #{email}, phone = #{phone}, email_verified = #{emailVerified},
    email_verified_at = #{emailVerifiedAt}, updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE users
SET password = #{encodedPassword}, updated_at = #{updatedAt}
WHERE id = #{userId}
